if __name__ == '__main__':
    #create grid with 1 and 0
    ts1 = time.time()
    list_str  = input_list.split("\n")
    rows = len(list_str)
    cols = len(list_str[0])
    grid = [[0 for col in range(cols)] for row in range(rows)]
    for i in range(rows):
        for j in range(cols):
            if list_str[i][j] == "@":
                grid[i][j] = 1
    accessibility_limit = 4

    #solution of part1:
    accessible_paper_rolls=0
    output_grid = [[0 for col in range(cols)] for row in range(rows)]
    for i in range(rows):
        for j in range(cols):
            if grid[i][j] == 1:
                paper_roll_cnt = 0
                for lr in range(-1,2):
                    for ud in range(-1,2):
                        if 0 <= i+ud < rows:
                            if 0 <= j+lr < cols:
                                if i+ud != i or j+lr != j:
                                    paper_roll_cnt += grid[i+ud][j+lr]
                output_grid[i][j] = paper_roll_cnt
                if paper_roll_cnt < accessibility_limit:
                    accessible_paper_rolls += 1

    print("solution part 1: ", accessible_paper_rolls)
    ts2 = time.time()
    print("execution time part 1: ", ts2-ts1)

    # solution of part2:
    accessible_paper_rolls = 0
    old_accessible_paper_rolls = -1
    while old_accessible_paper_rolls != accessible_paper_rolls:
        old_accessible_paper_rolls = accessible_paper_rolls
        output_grid = [[0 for col in range(cols)] for row in range(rows)]
        new_grid = [[0 for col in range(cols)] for row in range(rows)]
        for i in range(rows):
            for j in range(cols):
                paper_roll_cnt = 0
                if grid[i][j] == 1:
                    for lr in range(-1, 2):
                        for ud in range(-1, 2):
                            if 0 <= i + ud < rows:
                                if 0 <= j + lr < cols:
                                    if i + ud != i or j + lr != j:
                                        paper_roll_cnt += grid[i + ud][j + lr]
                    output_grid[i][j] = paper_roll_cnt
                    if paper_roll_cnt < accessibility_limit:
                        accessible_paper_rolls += 1
                if paper_roll_cnt < accessibility_limit:
                    new_grid[i][j] = 0
                else:
                    new_grid[i][j] = grid[i][j]
        for i in range(rows):
            for j in range(cols):
                grid[i][j] = new_grid[i][j]
    print("solution part 2: ", accessible_paper_rolls)
    ts3 = time.time()
    print("execution time part 2: ", ts3 - ts2)